-- {"options":{"parameterMappings":{},"isHidden":false,"position":{"autoHeight":true,"sizeX":6,"sizeY":12,"maxSizeY":1000,"maxSizeX":6,"minSizeY":1,"minSizeX":2,"col":0,"row":30}},"text":"","width":1,"dashboard_id":1,"visualization_id":6}
SELECT method,
       "from",
       "to",
       value,
       least((gas_premium * gas_limit) + (blk.parent_base_fee * gas_used), gas_limit * gas_fee_cap) AS gas_fees,
       gas_limit,
       gas_used,
       gas_fee_cap,
       gas_premium,
       parent_base_fee,
       block_cid,
       height AS epoch,
       msg.cid AS msg_cid
FROM filecoin.messages AS msg
LEFT JOIN filecoin.blocks blk ON blk.cid = block_cid
WHERE gas_used > 0 AND method = 0 AND value > 0
GROUP BY epoch,
         block_cid,
         gas_limit,
         gas_used,
         gas_fee_cap,
         gas_premium,
         parent_base_fee,
         msg.cid
ORDER BY epoch DESC;